import React,{useState} from 'react';
import './Content.css';

const Content = (Props,{notecontent,noteid}) => {
  const [note,setnote] = useState("");


  const handleChange = (e) =>{
    e.preventDefault();
    setnote(e.target.value);
    console.log(note);
  }

  const SaveHandler = (id) =>{
    console.log("Handling Save"+id);
}

  return (
    <div className="content">
      <textarea name="" id={noteid} value={notecontent} cols="164" rows="48" contentEditable={true} suppressContentEditableWarning={true} onChange={handleChange}></textarea>
        <div className="cbtn">
            <button className='btn' onClick={SaveHandler(noteid)}>Save</button>
        </div>
      </div>
  )
}

export default Content;
